import { Link } from "react-router-dom";
import { useState } from "react";

const SideBar = () => {
    const [currentTab, setCurrentTab] = useState('home');

    const handleTabChange = (e) => {
        const value = e.target.value;
        setCurrentTab(value);
    }

    return(
        <div className="h-screen w-56 pt-3 px-2 bg-stone-800">
          <ul className="flex flex-col gap-3 text-xl font-medium text-stone-200">
              <li>
              <Link currentTab={currentTab} onClick={e => handleTabChange(e)} value='home' to='/' className="transition ease-out duration-200  block p-2 rounded-lg hover:text-yellow-800 hover:bg-stone-900 hover:cursor-pointer">Início</Link>
              </li>
              <li >
              <Link onClick={e => handleTabChange(e)} value='recipes' to='/recipes' className="transition ease-out duration-200 block p-2 rounded-lg hover:text-yellow-800 hover:bg-stone-900 hover:cursor-pointer">Receitas</Link>
              </li>
              <li>
              <Link onClick={e => handleTabChange(e)} value='plans' to='/plans' className="transition ease-out duration-200 block p-2 rounded-lg hover:text-yellow-800 hover:bg-stone-900 hover:cursor-pointer">Planos</Link>
              </li>
              <li>
              <Link onClick={e => handleTabChange(e)} value='settings' to='/settings' className="transition ease-out duration-200 block p-2 rounded-lg hover:text-yellow-800 hover:bg-stone-900 hover:cursor-pointer">Configurações</Link>
              </li>
          </ul>  
        </div>
    )

}

export default SideBar;
