import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../contexts/authContext";
import axios from "axios";
import Post from "./post";
import moment from "moment";
import { useLocation } from "react-router-dom";

const Profile = () => {
    const { currentUser } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState();
    const location = useLocation();

    useEffect(() => {
        if(!user) fetchUser();
        else fetchPosts();
    }, [user])

    const fetchUser = async() => {
        try {
            const res = await axios.get(`//localhost:5000/user${location.pathname}`);
            setUser(res.data[0]);
        } catch(err) {
            console.error(err);
        }
    }

    const fetchPosts = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts/${user.id}`);
            const posts = res.data.map(post => ({
                ...post, 
                ingredients: JSON.parse(post.ingredients),
                date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
            }))
            setPosts(posts)
        } 
        catch(err) {
            console.log(err);
        }

    }

    return( 
        <div>
            <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                {user && <p>{user.name}</p>}
            </div>  
            <div className="flex flex-col">
                <div className="h-96 grid grid-rows-2 border-b border-stone-700">
                    <div className="bg-stone-500">
                    </div>
                        <div className="h-28">
                            {user && 
                                <img className='h-28 rounded-[80px]' src={`http://localhost:5000/upload/user/${user.img ? user.img : 'default.jpg'}`}/>}
                        </div>
                        {user && 
                            <div className="pl-4">
                                <p className="text-2xl">{user.name + "  "}</p>
                                <p>{'@' + user.username}</p>
                            </div>  
                        }
                </div>
                {posts && posts.map(post => <Post postObj={post} key={post.id}/>)}
            </div>
        </div>
    )
}


export default Profile;
