import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../contexts/authContext";
import axios from "axios";
import Post from "./post";
import moment from "moment";
import { Location, useLocation } from "react-router-dom";

const Profile = () => {
    const { user } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const location = useLocation();

    useEffect(() => {
        fetchData();
    }, [user])

    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts${location.pathname}`);
            const posts = res.data.map(post => ({
                ...post, 
                ingredients: JSON.parse(post.ingredients),
                date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
            }))
            setPosts(posts)
        } 
        catch(err) {
            console.log(err);
        }
    }

    return( 
        <div>
            <p>Inicio</p>
            <div className="flex flex-col">
                <div className="h-96 grid grid-rows-2 border-b border-stone-700">
                    <div className="bg-stone-500">
                    </div>
                    <div className="pl-4">
                        <p className="text-2xl">{user.name + "  "}</p>
                        <p>{'@' + user.username}</p>
                    </div>
                </div>
                {posts && posts.map(post => <Post postObj={post} key={post.id}/>)}
            </div>
        </div>
    )
}


export default Profile;
