import { useState } from 'react';
import { useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit }) => {
    const { register, unregister, setFocus, clearErrors, handleSubmit, formState: { errors } } = useForm();

    const [ingredients, setIngredients] = useState([]);
    const [description, setDescription] = useState(false);
    const [image, setImage] = useState(false);

    const addNewIngredientField = () => {
        const copyIngredientsArray = [...ingredients];
        copyIngredientsArray.push('ingredient' + ingredients.length + 1)
        setIngredients(copyIngredientsArray);
    }

    const removeNewIngredientField = (item, index) => {
        unregister(item);
        const copyIngredientsArray = [...ingredients];
        copyIngredientsArray.splice(index, 1)
        setIngredients(copyIngredientsArray)
    }

    const RemoveAllIngredientFields = () => {
        ingredients.map(ingredient => {
            unregister(ingredient)
        })
        setIngredients([]);
    }

    const addDescriptionField = () => {
        setDescription(true);
        setTimeout(() => setFocus('description'), 0)
    }

    const removeDescriptionField = (e) => {
        unregister('description');
        clearErrors('description');
        setDescription(false);
    }

    return(
    <form autoComplete='off' className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='flex flex-col items-stretch pl-3 box-border pb-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='py-2 placeholder:text-stone-400 text-lg bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
            {description && 
                <div className='flex flex-col justify-items-stretch w-4/5 bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    <label className='text-sm text-stone-200'>Descrição</label>
                    <input {...register('description')} className='mt-2 bg-inherit'/>
                    <button name='description' onClick={e => removeDescriptionField(e)}  className='text-sm'>Remover campo</button>
                </div>
            }
            {ingredients.length > 0 && 
                <ul className='flex flex-col w-4/5 bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    {ingredients.map((item, index) => (
                        <li key={index}>
                            <label className='text-sm text-stone-200'>Ingredientes</label>
                                <div className='flex gap-3'>
                                    <input {...register(item)} className='h-14 w-4/5 rounded-md border border-stone-700 mt-2 bg-inherit'/>
                                    <button onClick={() => removeNewIngredientField(item, index)} className='text-sm p-2 bg-stone-500'>-</button>
                                    {index === ingredients.length - 1 && 
                                        <button onClick={() => addNewIngredientField()} className='text-sm bg-stone-700 p-3'>+</button>
                                    }
                                </div>
                            <button name='ingredients' onClick={e => RemoveAllIngredientFields(e)} className='text-sm'>Remover campo</button>
                        </li>
                    ))}
                </ul>
            }
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <button onClick={(e) => addNewIngredientField(e)} className='justify-self-start text-stone-200 text-sm'>Adicionar ingredientes</button>
            <button onClick={(e) => addDescriptionField(e)} className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button className='justify-self-start text-stone-200 text-sm'>Adicionar foto</button>
            <input type='button' value='Fweet' className="mr-2 justify-self-end py-1 px-5 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600"/>
        </div>
    </form>

    )
}

export default FweetForm;
