import { useState } from 'react';
import { useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit }) => {
    const { register, unregister, setFocus, clearErrors, handleSubmit, formState: { errors } } = useForm();

    const [fields, setFields] = useState({
        ingredients: false,
        ingredientsArray: [],
        ingredientsCount: 0,
        description: false,
        image: false,
    })

    const addIngredient = () => {
        const copyIngredientsArray = {...fields}.ingredientsArray;
        copyIngredientsArray.append('ingredient' + fields.ingredientsCount + 1)
        setFields(fields => ({
            ...fields, 
            ingredientsArray: copyIngredientsArray,
            ingredientsCount: fields.ingredientsCount + 1
        }))
    }

    const addField = (e) => {
        e.preventDefault();
        const name = e.target.name;
        if(!fields[name]) {
            setFields(fields => ({...fields, [name]: true}));
            setTimeout(() => setFocus(name), 0);
        }
    }

    const removeField = (e) => {
        const name = e.target.name;
        if(fields[name]) {
            unregister(name);
            clearErrors();
            setFields(fields => ({...fields, [name]: false}));
        }
    }

    return(
    <form autoComplete='off' className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='flex flex-col items-stretch pl-3 box-border pb-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='py-2 placeholder:text-stone-400 text-lg bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
            {fields.description && 
                <div className='flex flex-col justify-items-stretch w-4/5 bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    <label className='text-sm text-stone-200'>Descrição</label>
                    <input {...register('description')} className='mt-2 bg-inherit'/>
                    <button name='description' onClick={e => removeField(e)}  className='text-sm'>Remover campo</button>
                </div>
            }
            {fields.ingredientsArray && 
                <ul className='flex flex-col justify-items-stretch w-4/5 bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    {fields.ingredientsArray.map((item, index) => (
                        <li>
                            <label className='text-sm text-stone-200'>Ingredientes</label>
                                <div className='flex'>
                                    <input {...register(item)} className='mt-2 bg-inherit'/>
                                    <button onClick={() => addIngredient()} className='text-sm bg-stone-700 p-3'>+</button>
                                </div>
                            <button name='ingredients' onClick={e => removeField(e)} className='text-sm'>Remover campo</button>
                        </li>
                    ))}
                </ul>
            }
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <button name='ingredients' onClick={(e) => {addField(e); addIngredient()}} className='justify-self-start text-stone-200 text-sm'>Adicionar ingredientes</button>
            <button name='description' onClick={(e) => addField(e)} className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button name='image' onClick={(e) => addField(e)} className='justify-self-start text-stone-200 text-sm'>Adicionar foto</button>
            <input type='button' value='Fweet' className="mr-2 justify-self-end py-1 px-5 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600"/>
        </div>
    </form>

    )
}

export default FweetForm;
