import Popup from "reactjs-popup"
import {  useContext } from 'react';
import { useForm } from 'react-hook-form'
import { AuthContext } from '../contexts/authContext';


const EditProfile = ({ closeModal }) => {
    const { currentUser } = useContext(AuthContext);
    const { register, handleSubmit, reset } = useForm();

    const handleClose = (e) => {
        e.preventDefault();
        closeModal();
        reset();
    };

    return(
        <form className="grid grid-rows-6 h-[450px] w-[600px] bg-stone-900 rounded-xl pb-8">
            <div className="flex items-center px-4">
                <button type='button' className="hover:bg-stone-600 rounded-full flex justify-center transition" onClick={(e) => handleClose(e)}>x</button>
                <h1 className="text-2xl font-sans font-medium antialiased tracking-tight text-white ">Editar perfil</h1>
            </div>
            <div className="bg-stone-500 row-span-2">
                <img className='h-32 p-5 relative top-1/2 rounded-[80px]' src={`http://localhost:5000/upload/user/${currentUser.img ? currentUser.img : 'default.jpg'}`}/>
            </div>
            <div className="flex flex-col row-start-5 row-span-2 px-4 pb-4 gap-3">
                <input 
                    className='rounded-md text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    placeholder="Nome de usuÃ¡rio"
                    {...register('name')}
                    />
                <input 
                    className='rounded-md text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    placeholder="Bio"
                    {...register('bio')}
                    />
            </div>
        </form>

    )
}

export default EditProfile;
