import TabHeader from "./tabHeader";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { useRef } from "react";
import { AuthContext } from "../contexts/authContext.js";

const Home = () => {
    const { user } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const postRef = useRef();

    useEffect(() => {
        fetchData();
    }, [])
    
    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts`, {
                params: {
                    id: user.id
                }
            });
            setPosts(res.data)
        } 
        catch(err) {
            console.log(err);
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        try {
            axios.post('//localhost:5000/posts', {
                user_id: user.id, 
                body: postRef.current.value,
                date: '21/02/2023'
            })
        }
        catch (err) {
            console.log(err);
        }
        postRef.current.value = '';
        fetchData();
    }

    return(
        <div className="">
            <TabHeader>
                <Link to='/'>In√≠cio</Link>
            </TabHeader>
            <form 
                className="p-3 grid border-b border-stone-700 bg-inherit"
                onSubmit={(e) => handleSubmit(e)}
                >
                <input className='bg-white text-black' ref={postRef}/>
            </form>
            <ul className="flex flex-col">
                {posts.map(post => 
                    <li key={post.id}>
                        <div className="h-16 pt-1 pl-3 grid border-b border-stone-700 justify-start grid-flow-col">
                            <div className="flex flex-col">
                                <div className="flex flex-row gap-3 items-center text-sm text-stone-500 [&_>:first-child]:text-white">
                                    <p>{post.body}</p>
                                    <p>{post.date}</p>
                                </div>
                            </div>
                        </div>
                    </li>
                )}

            </ul>
        </div>
    )
}



export default Home;
