import TabHeader from "./tabHeader";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { useRef } from "react";
import { AuthContext } from "../contexts/authContext.js";
import { useNavigate } from "react-router-dom";

const Home = () => {
    const { user } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const postRef = useRef();   
    const navigate = useNavigate();

    useEffect(() => {
        user ? fetchData() : navigate('/login');
    }, [])
    
    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts`, {
                params: {
                    id: user.id
                }
            });
            setPosts(res.data)
        } 
        catch(err) {
            console.log(err);
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('//localhost:5000/posts', {
                user_id: user.id, 
                body: postRef.current.value,
                date: '22/02/2023'
            })
        }
        catch (err) {
            console.log(err);
        }
        postRef.current.value = '';
        fetchData();
    }

    return(
        <div className="">
            <TabHeader>
                <Link to='/'>Início</Link>
            </TabHeader>
            <div className="flex py-2 pl-4 border-b border-stone-700 bg-inherit">
                <div className="w-14">img</div>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <input className='h-9 bg-inherit text-white' ref={postRef}/>
                    <button>Fweet</button>
                </form>
            </div>
            <ul className="flex flex-col">
                {testPosts.map(post => 
                    <li className="flex py-2 pl-4 border-b border-stone-700" key={post.id}>
                        <div className="w-14">img</div>
                        <div className="flex flex-col">
                            <div className="flex max-w-full gap-2 items-center">
                                <p className="font-bold">{post.user_name}</p>
                                <div className="flex flex-row text-sm text-stone-400 gap-1">
                                    <p >{post.user_username}</p>
                                    <p >{"· " + post.date}</p>
                                </div>
                            </div>
                            <h1 className="mt-1">{post.body.name}</h1>
                            <p className="font-medium mt-1">{'Ingredientes: '}</p>
                            {post.body.ingredients.map(ingredient => (
                                <p className="inline">{ingredient.quantity + " " + ingredient.unity + " " + ingredient.name}</p>
                            ))}
                            <p className="font-medium mt-2">{'Descrição: '}</p>
                            <p>{post.body.description}</p>
                        </div>
                    </li>
                )}

            </ul>
        </div>
    )
}

const post1 = {
    user_id: 6,
    user_name: 'Matheus',
    user_username: '@matheusplo',
    id: 2,
    date: '24/02/2023',
    body: {
        name: 'Cheesy vinegar',
        ingredients: [{
            name: 'cheese',
            quantity: 20,
            unity: 'g'
        }, {
            name: 'vinegar',
            quantity: 30,
            unity: 'ml'
        }],
        description: 'Put the vinegar into the cheese',
        time: {
            quantity: 5,
            unity: 'minutes'
        },
    },
    img: '',

}
const post2 = {
    user_id: 6,
    user_name: 'Matheus',
    user_username: '@matheusplo',
    id: 1,
    date: '24/02/2023',
    body: {
        name: 'Cooked pasta with tomato sauce',
        ingredients: [{
            name: 'pasta',
            quantity: 100,
            unity: 'g'
        }, {
            name: 'tomatoes',
            quantity: 2,
            unity: 'whole'
        }],
        description: 'Cook the pasta and then put the tomatoes into the pasta',
        time: {
            quantity: 15,
            unity: 'minutes'
        },
    img: '',
    }
}

const testPosts = [post1, post2];

export default Home;
