import axios from "axios";
import moment from "moment";
import { useContext, useEffect, useState } from "react";
import { useFieldArray, useForm } from "react-hook-form";
import { AuthContext } from "../../contexts/authContext";

export const useWrite = (fetchPosts) => {
    const { currentUser } = useContext(AuthContext);

    const { 
        register, 
        unregister, 
        handleSubmit, 
        reset,
        control,
        setFocus,
        formState: { isValid }
    } = useForm();    

    const { 
        fields: ingredientsArray, 
        append, 
        remove
     } = useFieldArray({
            control,
            name: 'ingredients'
        });
    
    const uploadPostImg = async (file) => {
        try {
            const formData = new FormData();
            formData.append("file", file);
            const res = await axios.post('//localhost:5000/upload/post', formData);
            return res.data;
        } 
        catch(err) {
            console.error(err);
        }
    }
        
    const onSubmit = async (data) => {
        const { title, description, ingredients, file } = data;
        const fileUrl = file[0] ? await uploadPostImg(file[0]) : '';
        const postObj = {
            user_id: currentUser.id, 
            title,
            description,
            ingredients: JSON.stringify(ingredients),
            date: moment().format("YYYY-MM-DD HH:mm:ss"),
            file: fileUrl
        }
        axios.post('//localhost:5000/posts', postObj)
        .then(fetchPosts());
        reset();
    }
    
    const [description, setDescription] = useState(false);
    
    useEffect(() => {
        if(!description) {
            unregister('description');
        }
        else {
            setFocus('description');
        }
    }, [description])    

    return { 
        register, 
        onSubmit: handleSubmit(onSubmit), 
        description,
        setDescription, 
        ingredientsArray, 
        isValid, 
        append, 
        remove 
    }
}
