import TabHeader from "./tabHeader";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { useRef } from "react";
import { AuthContext } from "../contexts/authContext.js";
import { useNavigate } from "react-router-dom";
import FweetForm from "./fweetForm";
import Post from "./post";

const Home = () => {
    const { user } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        user ? fetchData() : navigate('/login');
    }, [])
    
    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts`, {
                params: {
                    id: user.id
                }
            });
            setPosts(res.data)
        } 
        catch(err) {
            console.log(err);
        }
    }

    const onSubmit = async (data) => {
        const { id: user_id, name: user_name, username: user_username } = user;
        console.log(data)
        try {
            await axios.post('//localhost:5000/posts', {
                user_id, 
                user_name,
                user_username,
                data: JSON.stringify(data),
                date: new Date().toISOString().slice(0, 19).replace('T', ' ')
            })
        }
        catch (err) {
            console.log(err);
        }
        fetchData();
    }

    return(
        <div className="">
            <TabHeader>
                <Link to='/'>In√≠cio</Link>
            </TabHeader>
            <div className="grid grid-flow-col grid-cols-10 py-2 pl-4 border-b border-stone-700 bg-inherit">
                <div className="col-span-1">img</div>
                <FweetForm onSubmit={onSubmit}/>
            </div>
            <ul className="flex flex-col">
                {posts.map(post => <Post post={post} key={post.id}/>)}
            </ul>
        </div>
    )
}

const post1 = {
    user_id: 6,
    user_name: 'Matheus',
    user_username: '@matheusplo',
    id: 2,
    date: '24/02/2023',
    body: {
        name: 'Cheesy vinegar',
        ingredients: [{
            name: 'cheese',
            quantity: 20,
            unity: 'g'
        }, {
            name: 'vinegar',
            quantity: 30,
            unity: 'ml'
        }],
        description: 'Put the vinegar into the cheese',
        time: {
            quantity: 5,
            unity: 'minutes'
        },
    },
    img: '',

}
const post2 = {
    user_id: 6,
    user_name: 'Matheus',
    user_username: '@matheusplo',
    id: 1,
    date: '24/02/2023',
    body: {
        name: 'Cooked pasta with tomato sauce',
        ingredients: [{
            name: 'pasta',
            quantity: 100,
            unity: 'g'
        }, {
            name: 'tomatoes',
            quantity: 2,
            unity: 'whole'
        }],
        description: 'Cook the pasta and then put the tomatoes into the pasta',
        time: {
            quantity: 15,
            unity: 'minutes'
        },
    img: '',
    }
}

const testPosts = [post1, post2];

export default Home;
