import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../contexts/authContext";
import axios from "axios";
import Post from "./post";
import moment from "moment";
import { useLocation } from "react-router-dom";
import Popup from 'reactjs-popup';
import EditProfile from "./editProfile";

const Profile = () => {
    const { currentUser } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState();
    const location = useLocation();
    const [openPopup, setOpenPopup] = useState(false);
    
    useEffect(() => {
        if(!user) fetchUser();
        else fetchPosts();
    }, [user])

    const fetchUser = async() => {
        try {
            const res = await axios.get(`//localhost:5000/user${location.pathname}`);
            setUser(res.data[0]);
        } catch(err) {
            console.error(err);
        }
    }

    const fetchPosts = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts/${user.id}`);
            const posts = res.data.map(post => ({
                ...post, 
                ingredients: JSON.parse(post.ingredients),
                date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
            }))
            setPosts(posts)
        } 
        catch(err) {
            console.log(err);
        }

    }

    return( 
        <div>
            <Popup
                open={openPopup}
                onOpen={() => setOpenPopup(true)}
                onClose={() => setOpenPopup(false)}
                overlayStyle={{
                    'background-color': 'rgba(0,0,0,0.5)',
                    'display': 'flex',
                    'justify-items': 'center',
                    'align-items': 'center'
                    }}
            >   
                <EditProfile/>
            </Popup>
            <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                {user && <p>{user.name}</p>}
            </div>  
            <div className="flex flex-col">
                <div className="h-[440px] grid grid-rows-5 border-b border-stone-700">
                    <div className="bg-stone-500 row-span-2">
                        {user &&
                            <img className='h-32 ml-4 relative top-2/3 rounded-[80px]' src={`http://localhost:5000/upload/user/${user.img ? user.img : 'default.jpg'}`}/>
                        }
                    </div>
                    {user && user.name === currentUser.name && 
                        <button 
                            onClick={() => setOpenPopup(true)}
                            className='justify-self-end h-10 hover:bg-stone-600 hover:cursor-pointer hover:border-stone-500 py-1 px-4 text-md transition ease-in-out duration-300 font-bold rounded-3xl my-2 mr-2 border border-stone-600'
                        >
                            Editar perfil
                        </button>
                    }
                    {user && 
                            <div className="row-start-4 pl-5 flex flex-col">
                                <p className=" text-2xl">{user.name + "  "}</p>
                                <p className="text-stone-400">{'@' + user.username}</p>
                            </div>  
                    }
                </div>
                {posts && posts.map(post => <Post postObj={post} key={post.id}/>)}
            </div>
        </div>
    )
}


export default Profile;
