import { createContext, useState } from "react";
import { Outlet } from "react-router-dom";
import Popup from "reactjs-popup";
import EditProfile from "./components/editProfile";
import NavBar from "./components/navBar";



export const PopupContext = createContext();

const Layout = () => {

    return (
        <div className='flex flex-row justify-center '> 
            <NavBar/>
            <div className='flex flex-col min-h-screen w-[600px] border-r border-l border-stone-700'>
                <PopupContext.Provider value={{openPopup, setOpenPopup}}>
                    <Outlet/>
                </PopupContext.Provider>

            </div>
            <div className="ml-4 sticky top-10  h-60 w-60 bg-stone-700 rounded-2xl p-3">
                <p className='text-lg font-bold'>Receitas em alta</p>
            </div>
        </div>
      )
}

export default Layout;
