import { useContext } from "react";
import { Outlet } from "react-router-dom";
import { useLocation } from "react-router-dom";
import TabLink from "./components/tabLink";
import { AuthContext } from "./contexts/authContext";
import { useNavigate } from "react-router-dom";

const PageLayout = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { user, logout } = useContext(AuthContext)

    const handleLogout = () => {
        logout();
        navigate('/login')
    }

    return (
        <div className="grid grid-flow-col justify-center bg-stone-800 text-white"> 
            <div className="grid grid-flow-row w-60 border-r border-stone-700 text-[22px] font-sans font-normal antialiased tracking-tight text-white"> 
                <nav className='grid grid-cols-4 flex-col'>
                    <ul className="flex flex-col mt-10 ">
                        <li>
                            <TabLink label='Início' pathname='/' location={location}/>
                        </li>
                        <li>
                            <TabLink label='Explorar' pathname='/explore' location={location}/>
                        </li>
                        <li>
                            <TabLink label='Perfil' pathname='/profile' location={location}/>
                        </li>
                        <li>
                            <TabLink label='Configurações' pathname='/settings' location={location}/>
                        </li>
                        <button className='mt-8 w-5/6 h-10 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600 text-center'>
                            Fweet
                        </button>
                    </ul>  
                    <div onClick={() => handleLogout()} className='col-start-4 w-5/6 text-base mb-10 hover:bg-stone-700 hover:cursor-pointer rounded-3xl py-2 px-6'>
                        <p>{user.name}</p>
                        <p>Sair</p>
                    </div>
                </nav>
            </div>
            <div className='w-[600px] border-r border-stone-700'>
                <Outlet/>
            </div>
            <div className='mx-4 mt-10 w-80 bg-stone-700 h-80 rounded-2xl py-2 px-6'>
                <p className='text-lg font-bold'>Receitas em alta</p>
            </div>
        </div>
      )
}

export default PageLayout;
