import TabHeader from "./tabHeader";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { AuthContext } from "../contexts/authContext.js";
import { useNavigate } from "react-router-dom";
import FweetForm from "./fweetForm";
import Post from "./post";

const Home = () => {
    const { user } = useContext(AuthContext);
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        user ? fetchData() : navigate('/login');
    }, [])
    
    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/posts`, {
                params: {
                    id: user.id
                }
            });
            const posts = res.data.map(post => 
                ({...post, ingredients: JSON.parse(post.ingredients)})
            )
            console.log(posts)
        } 
        catch(err) {
            console.log(err);
        }
    }

    const sendData = async (data) => {
        const { id: user_id, name: user_name, username: user_username } = user;
        const { title, description, ingredients } = data;
        try {
            await axios.post('//localhost:5000/posts', {
                user_id, 
                user_name,
                user_username,
                title,
                description,
                ingredients: JSON.stringify(ingredients),
                date: new Date().toISOString().slice(0, 19).replace('T', ' ')
            })
        }
        catch (err) {
            console.log(err);
        }
        fetchData();
    }

    return(
        <div className="">
            <TabHeader>
                <Link to='/'>In√≠cio</Link>
            </TabHeader>
            <div className="grid grid-flow-col grid-cols-10 py-2 pl-4 border-b border-stone-700 bg-inherit">
                <div className="col-span-1">img</div>
                <FweetForm sendData={sendData}/>
            </div>
            <div className="flex flex-col">
                {posts.map(post => <Post post={post}/>)}
            </div>
        </div>
    )
}

export default Home;
