import axios from "axios";

export const updateUser = async ({ data, currentUser }) => {
    const { name, bio, profileImage, backgroundImage } = data;
    const profileImageUrl = profileImage[0] ? await uploadProfileImg(profileImage[0]) : currentUser.profile_img;
    const backgroundImageUrl = backgroundImage[0] ? await uploadBackgroundImg(backgroundImage[0]) : currentUser.profile_bg_img;

    const userData = {
        name,
        username: currentUser.username,
        bio,
        id: currentUser.id,
        profile_img: profileImageUrl,
        profile_bg_img: backgroundImageUrl
    };
    
    return axios.post('//localhost:5000/user', userData);
}

const uploadProfileImg = async (file) => {
    const formData = new FormData();
    formData.append("file", file);
    const res = await axios.post('//localhost:5000/upload/userProfile', formData);
    return res.data;
}

const uploadBackgroundImg = async (file) => {
    const formData = new FormData();
    formData.append("file", file);
    const res = await axios.post('//localhost:5000/upload/userBackground', formData);
    return res.data;
}
