import TabHeader from "./tabHeader";


const user1 = {
    name: 'Matheus',
    recipes: [
        {
            name:'recipe1',
            date:'19/02/2023',
            description: 'chicken and rice'
        }
    ]
}
const user2 = {
    name: 'Ana',
    recipes: [
        {
            name:'recipe1',
            date:'19/02/2023',
            description: 'scrambled eggs with tomato'
        }
    ]
}
const users = [user1, user2];

const Home = () => {
    return(
        <div className="">
            <TabHeader 
                mainText='Início'
                subText='Veja as atualizações de quem você está seguindo'
            />
            <ul className="flex flex-col">
                {users.map((user, index) => 
                    <li key={index}>
                        <div className="h-16 pt-1 pl-3 grid border-b border-stone-600 justify-start grid-flow-col">
                            <div className="w-12">img</div>
                            <div className="flex flex-col">
                                <div className="flex flex-row gap-3">
                                    {user.name}
                                    {user.recipes[0].date}
                                </div>
                                <div>
                                    {user.recipes[0].name}
                                </div>
                            </div>
                        </div>
                    </li>
                )}

            </ul>
        </div>
    )
}



export default Home;
