import { useState, useEffect } from 'react';
import { useFieldArray, useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit }) => {
    const { 
        register, 
        unregister, 
        setFocus, 
        clearErrors, 
        handleSubmit, 
        watch,
        control,
        formState: { errors } } 
        = useForm({
            title: '',
            description: '',
            ingredients: []
        });
    
    const watchDescriptionCheck = watch('descriptionCheck');
    const watchIngredientsCheck = watch('ingredientsCheck');

    useEffect(() => {
        if(!watchDescriptionCheck) {
            unregister('description');
        }
    }, [watchDescriptionCheck])

    const { 
        fields: ingredientFieldsArray, 
        append, 
        remove
     } = useFieldArray({
            control,
            name: 'ingredients'
        });


    return(
    <form autoComplete='off' className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='flex flex-col items-stretch pl-3 box-border pb-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='py-2 placeholder:text-stone-400 text-lg bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
            {watchDescriptionCheck && 
                <div className='flex flex-col justify-items-stretch w-4/5 bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    <label className='text-sm text-stone-200'>Descrição</label>
                    <input {...register('description')} className='mt-2 bg-inherit'/>
                    <input type='checkbox' name='Remover campo' {...register('descriptionCheck')} className='text-sm'/>
                </div>
            }
            {watchIngredientsCheck && 
                <div className='flex flex-col w-[90%] bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-xl focus-within:border-stone-600 hover:border-stone-600'>
                    <label className='text-sm text-stone-200'>Ingredientes</label>
                    {ingredientFieldsArray.map((field, index) => (
                        <div key={field.id} className='h-14 mr-3 rounded-md border border-stone-700 mt-2 grid grid-flow-col grid-cols-6'>
                            <input {...register(`ingredients.${index}.name`)} className='pl-2 col-span-3 bg-inherit placeholder:text-stone-500 border-r border-stone-700' placeholder='Novo ingrediente'/>
                            <input {...register(`ingredients.${index}.quantity`)} className='pl-2 bg-inherit placeholder:text-stone-500 border-r border-stone-700' placeholder='Qtd'/>
                            <input {...register(`ingredients.${index}.unity`)} className='pl-2 bg-inherit placeholder:text-stone-500 ' placeholder='Uni'/>
                            {index === ingredientFieldsArray.length - 1 && 
                                <button onClick={() => append({name: '', quantity: '',  unity: ''})} className='text-sm bg-stone-700 p-3'>+</button>
                            }
                        </div>
                    ))}
                    <button name='ingredients' onClick={() => remove()} className='text-sm'>Remover campo</button>
                </div>
            }
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <input type='button' {...register('descriptionCheck')} value='Adicionar descrição'/>
            <input type='button' {...register('ingredientsCheck')} value='Adicionar ingredientes'/>
{/*             <button onClick={() => append({name: '', quantity: '',  unity: ''})} className='justify-self-start text-stone-200 text-sm'>Adicionar ingredientes</button>
            <button onClick={(e) => addwatchDescriptionCheck(e)} className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button className='justify-self-start text-stone-200 text-sm'>Adicionar foto</button>
 */}            <input type='button' value='Fweet' className="mr-2 justify-self-end py-1 px-5 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600"/>
        </div>
    </form>

    )
}

export default FweetForm;
