const Recipes = () => {
    return( 
        <div className='bg-gray-500'>
            <h1 className='text-4xl'>Lista de receitas</h1>
            <ul className='flex gap-5'>
                {recipesList.map((recipe, index) => 
                    <li className='bg-slate-400' key={index}>
                        {recipe.name}
                        <ul>
                            {recipe.ingredients.map((ingredient, index) => 
                                <li key={index}>{ingredient.name + ' ' + ingredient.quantity}</li>
                            )}
                        </ul>
                    </li>
                )}
            </ul>
        </div>
    )
}

const recipe1 = {
    name: 'recipe1',
    ingredients: [
        {
            name: 'cheese',
            quantity: 2
        },
        {
            name:'bread',
            quantity: 3
        }
    ]
}
const recipe2 = {
    name: 'recipe2',
    ingredients: [
        {
            name: 'butter',
            quantity: 1
        },
        {
            name:'salt',
            quantity: 1
        }
    ]
}

const recipesList = [recipe1, recipe2];

export default Recipes;
