import { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext.js";
import Write from '../post/write';
import { useQuery } from "@tanstack/react-query";
import Post from '../post/post';
import axios from "axios";
import moment from "moment";

const Home = () => {
    const { currentUser } = useContext(AuthContext);
    const navigate = useNavigate();

    const { isFetching, isStale, isLoading, error, data } = useQuery({queryKey: ['posts'], queryFn: () => 
        axios.get('//localhost:5000/posts/', {
            params: {
                id: currentUser.id
            }
        }).then(res => 
            res.data.map(post => ({
                ...post, 
                ingredients: JSON.parse(post.ingredients),
                date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
            }))
        )
        });

    useEffect(() => {
        !currentUser && navigate('/login');
        console.log(isStale)
    }, [isFetching])
    
    return(
        <div className="">
            <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                <p>Inicio</p>
            </div>  
            <div className="flex flex-col"> 
            <Write/>
            {isLoading ? 
                <>
                </>
                :
                data.map(post => 
                    <Post key={post.id} postObj={post}/>
                )
            }
            </div>
        </div>
    )
}

export default Home;
