import { useContext } from "react";
import { AuthContext } from "../contexts/authContext";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
    const {user, setUser} = useContext(AuthContext)
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        try {
            axios.post('//localhost:5000/auth', {
                email: email,
                username: username,
                password: password,
            })
        }
        catch (err) {
            console.log(err);
        }
    }

    return(
        <div className="bg-stone-800 grid justify-center items-center min-h-screen">
            <form onSubmit={(e) => handleSubmit(e)} className="grid w-96 h-96 p-10 bg-stone-500 rounded-lg">
                <input 
                    className='outline-none h-6'
                    placeholder="Email"
                    onChange={(e) => setEmail(e.target.value)} 
                    value={email}
                    />
                <input 
                    className='outline-none h-6'
                    placeholder="Nome de usuÃ¡rio"
                    onChange={(e) => setUsername(e.target.value)} 
                    value={username}
                    />
                <input 
                    className='outline-none h-6'
                    placeholder="Senha"
                    type='password' 
                    onChange={e => setPassword(e.target.value)} 
                    value={password}
                    />
            </form>
        </div>
    )
}

export default Register;
