import { useContext, useState, useEffect } from "react"
import { AuthContext } from "../../contexts/authContext"
import { getFollowedById } from "./getFollowedById";

export const useFollow = (user) => {
    const { currentUser } = useContext(AuthContext);
    const [followed, setFollowed] = useState(false);

    useEffect(() => {
        if(user) {
            const followedUsers = getFollowedById(userId);
            followedUsers.forEach((item) => {
                if(item.follower_user_id === currentUser.id) {
                    setFollowed(true);
                } 
            })
        }
    }, [user])
 
    const handleFollow = async() => {
        try {
            followed ? 
                axios.delete('//localhost:5000/follow', {
                    data: {
                        follower_user_id: currentUser.id,
                        followed_user_id: user
                    }
                }).then(() => setFollowed(false))
            :
                axios.post('//localhost:5000/follow', {
                    follower_user_id: currentUser.id,
                    followed_user_id: user
                }).then(() => setFollowed(true))
        } catch(err) {
            console.error(err);
        }
    }

    return { followed, handleFollow }
}
