import Popup from "reactjs-popup"
import {  useContext, useEffect } from 'react';
import { useForm } from 'react-hook-form'
import { AuthContext } from '../contexts/authContext';
import axios from "axios";


const EditProfile = ({ onSubmitForm , onClose}) => {
    const { currentUser, setCurrentUser } = useContext(AuthContext);
    const { register, handleSubmit, reset, watch } = useForm({
        defaultValues: {
            name: currentUser.name,
            bio: currentUser.bio,
            profileImage: '',
        }
    });

    const imgWatch = watch('profileImage');

    useEffect(() => {
        console.log(imgWatch);
    }, [imgWatch])

    const handleClose = (e) => {
        e.preventDefault();
        reset();
        onClose();
    };

    const updateUser = async (res, data) => {
        setCurrentUser(currentUser => ({
            ...currentUser,
            name: data.name,
            bio: data.bio
        }));
    }
    
    const onSubmit = async (data) => {
        const res = await axios.post('//localhost:5000/user', {
            name: data.name,
            bio: data.bio,
            id: currentUser.id
        })
        updateUser(res, data);
        reset();
        onSubmitForm();
    }

    return(
        <form autoComplete="off" className="flex flex-col w-[600px] bg-stone-900 rounded-xl pb-8" onSubmit={handleSubmit(onSubmit)}>
            <div className="flex items-center py-3 px-4">
                <button type='button' className="hover:bg-stone-600 rounded-full inline-flex flex-col text-center items-center justify-center p-1 transition" onClick={(e) => handleClose(e)}>x</button>
                <h1 className="ml-4 text-2xl font-sans font-medium antialiased tracking-tight text-white ">Editar perfil</h1>
                <button type="submit">Salvar</button>
            </div>
            <div className="bg-stone-500 h-44">
                <input type='file' {...register('profileImage')}/>
                <img className='h-32 p-5 relative top-2/3 rounded-[80px]' src={`http://localhost:5000/upload/user/${currentUser.img ? currentUser.img : currentUser.img}`}/>
            </div>
            <div className="flex flex-col pt-14 px-4 gap-2">
                <label htmlFor="name">Nome</label>
                <input 
                    id='name'
                    placeholder="Digite aqui... "
                    className='placeholder:text-stone-500 rounded-md text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    {...register('name')}
                    />
                <label htmlFor="bio">Biografia</label>
                <input 
                    id='bio'
                    placeholder="Digite aqui... "
                    className='placeholder:text-stone-500 rounded-md text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    {...register('bio')}
                    />
            </div>
        </form>

    )
}

export default EditProfile;
