import { useState } from 'react';
import { useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit }) => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const [fields, setFields] = useState({
        ingredients: false,
        description: false,
        image: false,
    })

    return(
    <form className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='flex flex-col items-stretch pl-3 box-border pb-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='py-2 placeholder:text-stone-400 text-lg bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
            <div className='w-96 bg-inherit mt-1 p-5 border border-stone-700 rounded-xl'>
                <input {...register('description')} className=''/>
            </div>
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <button onClick={() => setFields({...fields, ingredients: true})} className='justify-self-start text-stone-200 text-sm'>Adicionar ingredientes</button>
            <button onClick={() => setFields({...fields, description: true})} className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button onClick={() => setFields({...fields, image: true})} className='justify-self-start text-stone-200 text-sm'>Adicionar foto</button>
            <input type='button' value='Fweet' className="mr-2 justify-self-end py-1 px-5 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600"/>
        </div>
    </form>

    )
}

export default FweetForm;
