import { useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit, ref }) => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm();

    return(
    <form className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <input {...register('description', { required: true })} className='border-b border-stone-700 placeholder:text-stone-400 text-lg h-9 bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
        <div className="flex justify-end mr-3">
            <button className="py-1 px-12 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600">Fweet</button>
        </div>
    </form>

    )
}

export default FweetForm;
