import TabHeader from "./tabHeader";


const user1 = {
    name: 'Matheus',
    recipes: [
        {
            name:'recipe1',
            description: 'chicken and rice'
        }
    ]
}
const user2 = {
    name: 'Ana',
    recipes: [
        {
            name:'recipe1',
            description: 'scrambled eggs with tomato'
        }
    ]
}
const users = [user1, user2];

const Home = () => {
    return(
        <div className="">
            <TabHeader 
                mainText='Início'
                subText='Veja as atualizações de quem você está seguindo'
            />
            <ul className="flex flex-col">
                {users.map((user, index) => 
                    <li key={index} className="h-12 pt-4 pl-3 grid grid-flow-col border-b border-stone-600">
                        <div>
                            {user.name}
                        </div>
                        <div>
                            {user.recipes[0].name}
                        </div>
                    </li>
                )}

            </ul>
        </div>
    )
}



export default Home;
