import { useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit, ref }) => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm();

    return(
    <form className="flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='box-border py-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='ml-3 w-full placeholder:text-stone-400 text-lg h-9 bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <button className='justify-self-start text-stone-200 text-sm'>Adicionar ingredientes</button>
            <button className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button className="mr-2 justify-self-end py-1 px-12 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600">Fweet</button>
        </div>
    </form>

    )
}

export default FweetForm;
