import { Outlet } from "react-router-dom";
import { Link } from "react-router-dom";
import NavBar from "./components/navBar";
import { useLocation } from "react-router-dom";

const Layout = () => {
    const location = useLocation();

    const navigationsLabels = {
        '/': 'Início',
        '/explore': "Explorar",
        '/profile': 'Perfil',
        '/settings': 'Configurações'
    }

    return (
        <div className="flex flex-row justify-center"> 
            <NavBar/>
            <div className='flex flex-col min-h-screen w-[600px] border-r border-stone-700'>
                <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                    <Link to={location}>{navigationsLabels[location.pathname]}</Link>
                </div>  
                <Outlet/>
            </div>
            <div className="ml-4 sticky top-  h-60 w-60 bg-stone-700 rounded-2xl p-3">
                <p className='text-lg font-bold'>Receitas em alta</p>
            </div>
        </div>
      )
}

export default Layout;
