import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../contexts/authContext";
import axios from "axios";
import Post from "./post";
import moment from "moment";
import { useLocation } from "react-router-dom";

const Profile = () => {
    const { currentUser } = useContext(AuthContext);
    const [user, setUser] = useState();
    const location = useLocation();

    useEffect(() => {
        fetchData();
    }, [])


    const fetchData = async() => {
        try {
            const res = await axios.get(`//localhost:5000/users${location.pathname}`);
            console.l
            const user = res.data[0].posts.map(post => ({
                ...post, 
                ingredients: JSON.parse(post.ingredients),
                date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
            }))
            setUser(user)
        } 
        catch(err) {
            console.log(err);
        }

    }

    return( 
        <div>
            <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                {user && <p>{user.name}</p>}
            </div>  
            <div className="flex flex-col">
                <div className="h-96 grid grid-rows-2 border-b border-stone-700">
                    <div className="bg-stone-500">
                    </div>
                        {user && 
                            <div className="pl-4">
                                <p className="text-2xl">{user.name + "  "}</p>
                                <p>{'@' + user.username}</p>
                            </div>  
                        }
                </div>
                {user && user.posts.map(post => <Post postObj={post} key={post.id}/>)}
            </div>
        </div>
    )
}


export default Profile;
