export const unfollowUser = async() => {
    await axios.delete('//localhost:5000/follow', {
        data: {
            follower_user_id: currentUser.id,
            followed_user_id: user
        }
    })
}

export const followUser = async() => {
    await axios.post('//localhost:5000/follow', {
        follower_user_id: currentUser.id,
        followed_user_id: user
    })
}
