import { useContext } from "react";
import { AuthContext } from "../contexts/authContext";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const { login } = useContext(AuthContext)
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        await login(name, password);
        navigate('/');
    }

    return(
        <div className="bg-stone-800 grid justify-center items-center min-h-screen">
            <form onSubmit={e => handleSubmit(e)} className="grid w-96 h-96 p-10 bg-stone-900 rounded-lg">
                <h1 className="text-3xl font-sans font-medium antialiased tracking-tight text-white ">Entrar no fwitter</h1>
                <input 
                    className='align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 placeholder:text-lg focus:placeholder:text-sm focus:placeholder:align-text-top'
                    placeholder="Nome de usuÃ¡rio"
                    onChange={(e) => setName(e.target.value)} 
                    value={name}
                    />
                <input 
                    className='outline-none h-14 placeholder:top-4'
                    placeholder="Senha"
                    type='password' 
                    onChange={e => setPassword(e.target.value)} 
                    value={password}
                    />
                <button>submit</button>
            </form>
        </div>
    )
}

export default Login;
