const Recipes = () => {
    return(
        <ul>
            {recipesList.map((recipe, index) => 
                <div className='bg-slate-400' key={index}>
                    {recipe.name}
                    Nome da receita    Quantidade
                    {recipe.ingredients.map((ingredient, index) => 
                        <li key={index}>{ingredient.name + ' ' + ingredient.quantity}</li>
                    )}
                </div>
            )}
        </ul>
    )
}

const recipe1 = {
    name: 'recipe1',
    ingredients: [
        {
            name: 'cheese',
            quantity: 2
        },
        {
            name:'bread',
            quantity: 3
        }
    ]
}
const recipe2 = {
    ingredients: [
        {
            name: 'butter',
            quantity: 1
        },
        {
            name:'salt',
            quantity: 1
        }
    ]
}

const recipesList = [recipe1, recipe2];

export default Recipes;
