import axios from "axios";
import { createContext, useEffect } from "react";
import { useState } from "react";

export const AuthContext = createContext();

const AuthProvider = ({ children }) => {
    const [currentUserId, setCurrentUserId] = useState(
        JSON.parse(localStorage.getItem('user_id')) || null
        );

    useEffect(() => {
        localStorage.setItem('user_id', JSON.stringify(currentUserId))
    }, [currentUserId])

    const login = async (username, password) => {
        const res = await axios.post('//localhost:5000/auth/login', {
            username,
            password
        });
        
        setCurrentUserId(res.data);
    }

    const logout = async() => {
        setCurrentUserId(null);
    }

    return (
        <AuthContext.Provider value={{ currentUserId, login, logout }}>
            {children}
        </AuthContext.Provider>
    )
}

export default AuthProvider;
