import { Outlet } from "react-router-dom";
import { Link } from "react-router-dom";
import NavBar from "./components/navBar";
import { useLocation } from "react-router-dom";

const Layout = () => {
    const location = useLocation();
    console.log(location)

    const navigationsLabels = {
        '/': 'Início',
        '/explore': "Explorar",
        '/profile': 'Perfil',
        '/settings': 'Configurações'
    }

    return (
        <div className="flex flex-row justify-center"> 
            <nav className="w-60 border-r border-stone-700 ">
                <NavBar/>
            </nav>
            <div className='flex flex-col min-h-screen w-[600px] border-r border-stone-700'>
                <div className='fixed w-[600px] z-20 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl bg-stone-900'>
                    <Link to={location}>{navigationsLabels[location.pathname]}</Link>
                </div>  
                <Outlet/>
            </div>
            <div className="w-60 mx-4 mt-10">
                <div className="fixed h-80 w-60 bg-stone-700 rounded-2xl p-3">
                    <p className='text-lg font-bold'>Receitas em alta</p>
                </div>
            </div>
        </div>
      )
}

export default Layout;
