import { useContext } from "react";
import { AuthContext } from "../contexts/authContext";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const Register = () => {
    const {user, setUser} = useContext(AuthContext)
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('//localhost:5000/auth/register', {
                email,
                name,
                password,
            })
        }
        catch (err) {
            console.log(err);
        }
    }

    return(
        <div className="bg-stone-800 grid justify-center items-center min-h-screen">
            <form onSubmit={(e) => handleSubmit(e)} className="flex flex-col w-96 p-10 bg-stone-900 rounded-lg">
            <h1 className="text-3xl font-sans font-medium antialiased tracking-tight text-white">Criar conta</h1>
                <input 
                    className='mt-10 text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    placeholder="Email"
                    onChange={(e) => setEmail(e.target.value)} 
                    value={email}
                    />
                <input 
                    className='mt-6 text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    placeholder="Nome de usuário"
                    onChange={(e) => setName(e.target.value)} 
                    value={name}
                    />
                <input 
                    className='mt-6 text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                    placeholder="Senha"
                    type='password' 
                    onChange={e => setPassword(e.target.value)} 
                    value={password}
                    />
                <div>
                    <button className="mt-10 bg-gray-50 hover:bg-gray-100 active:bg-gray-200 rounded-2xl font-bold w-full h-9">Avançar</button>
                </div>
            <div className="flex">
                <p>{'Já tem conta?    '}</p>
                <Link to='/login'>Entrar</Link>
            </div>
            </form>
        </div>
    )
}

export default Register;
