import TabHeader from "./tabHeader";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";
import { useRef } from "react";

const Home = () => {
    const [posts, setPosts] = useState([]);
    const postRef = useRef();
    
    useEffect(() => {
        const fetchData = async() => {
            try {
                const res = await axios.get("//localhost:5000/posts");
                setPosts(res.data)
            } 
            catch(err) {
                console.log(err);
            }
        }
        fetchData();
    }, [])

    return(
        <div className="">
            <TabHeader>
                <Link to='/'>In√≠cio</Link>
            </TabHeader>
            <form 
                className="p-3 grid border-b border-stone-700 "
                onSubmit={() => console.log(postRef.current.value)}
                >
                <input className='text-blue-500' ref={postRef}/>
            </form>
            <ul className="flex flex-col">
                {posts.map(post => 
                    <li key={post.id}>
                        <div className="h-16 pt-1 pl-3 grid border-b border-stone-700 justify-start grid-flow-col">
                            <div className="flex flex-col">
                                <div className="flex flex-row gap-3 items-center text-sm text-stone-500 [&_>:first-child]:text-white">
                                    <p>{post.body}</p>
                                    <p>{post.date}</p>
                                </div>
                            </div>
                        </div>
                    </li>
                )}

            </ul>
        </div>
    )
}



export default Home;
