import { useState, useEffect } from 'react';
import { useFieldArray, useForm } from 'react-hook-form'

const FweetForm = ({ onSubmit }) => {
    const { 
        register, 
        unregister, 
        handleSubmit, 
        control,
        setFocus,
        watch,
    } = useForm({
        title: '',
        description: '',
        ingredients: []
    });    

    const { 
        fields: ingredientFieldsArray, 
        append, 
        remove
     } = useFieldArray({
            control,
            name: 'ingredients'
        });

    const [description, setDescription] = useState(false)

    useEffect(() => {
        if(!description) {
            unregister('description');
        }
        else {
            setFocus('description');
        }
    }, [description])    

    return(
    <form autoComplete='off' className="col-span-9 flex flex-col flex-grow" onSubmit={handleSubmit(onSubmit)}>
        <div className='flex flex-col pl-3 box-border pb-3 border-b border-stone-700'>
            <input {...register('title', { required: true })} className='py-2 placeholder:text-stone-400 text-lg bg-inherit text-white' placeholder='Qual receita você está fazendo agora?'/>
            {description && 
                <div className='flex flex-col  w-[90%] bg-inherit mt-2 py-2 pl-4 border border-stone-700 rounded-md focus-within:border-stone-600 hover:border-stone-600'>
                    <label className='text-sm text-stone-200'>Descrição</label>
                    <input {...register('description')} className='mt-2 bg-inherit'/>
                    <button onClick={() => setDescription(false)} className='text-sm'>Remover campo</button>
                </div>
            }
            {ingredientFieldsArray.length > 0 && ingredientFieldsArray.map((field, index) => (
                <div key={field.id} className='h-14 w-[90%] mt-2 grid grid-flow-col grid-cols-6'>
                    <input {...register(`ingredients.${index}.name`)} className='rounded-l-md border-t border-l border-b border-stone-700 pl-2 col-span-3 bg-inherit placeholder:text-stone-500 border-r ' placeholder='Nome do ingrediente'/>
                    <input {...register(`ingredients.${index}.quantity`)} className='pl-2 bg-inherit placeholder:text-stone-500 border-r border-stone-700' placeholder='Qtd'/>
                    <input {...register(`ingredients.${index}.unity`)} className='pl-2 bg-inherit placeholder:text-stone-500 ' placeholder='Uni'/>
                    <button onClick={() => remove(index)} className='text-sm bg-stone-700'>-</button> 
                </div>
            ))}
        </div>
        <div className="grid grid-flow-col mt-2 items-end">
            <button onClick={(e) => {e.preventDefault(); setDescription(true)}} className='justify-self-start text-stone-200 text-sm'>Adicionar descrição</button>
            <button onClick={(e) => {e.preventDefault(); append({ name: '', quantity: '', unity: '' })}} className='justify-self-start text-stone-200 text-sm'>Adicionar ingrediente</button>
            <input type='button' value='Fweet' className="mr-2 justify-self-end py-1 px-5 text-lg transition ease-out duration-100 font-bold rounded-3xl bg-stone-500 hover:cursor-pointer hover:bg-stone-600"/>
        </div>
    </form>

    )
}

export default FweetForm;
