import Popup from "reactjs-popup"
import {  useContext } from 'react';
import { useForm } from 'react-hook-form'
import { AuthContext } from '../contexts/authContext';


const EditProfile = () => {
    const { currentUser } = useContext(AuthContext);
    const { register, handleSubmit } = useForm();

    return(
        <form className="grid grid-rows-10 h-[440px] w-[600px] bg-stone-900 rounded-lg">
            <h1 className="text-3xl font-sans font-medium antialiased tracking-tight text-white ">Editar perfil</h1>
            <div className="bg-stone-500 row-span-4">
                <img className='h-14 top-2/3 rounded-[80px]' src={`http://localhost:5000/upload/user/${currentUser.img ? currentUser.img : 'default.jpg'}`}/>
            </div>
            <input 
                className=' text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                placeholder="Nome de usuÃ¡rio"
                {...register('name')}
                />
            <input 
                className=' text-gray-300 placeholder:text-gray-300 align-middle outline-none h-14 bg-inherit border border-stone-700 py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors'
                placeholder="Bio"
                {...register('bio')}
                />
        </form>

    )
}

export default EditProfile;
