    return( 
        <div className='bg-orange-50'>
            <div className='flex flex-col'>
                <div className='mt-5 ml-4 text-gray-900'>
                    <h1 className='font-semibold text-3xl'>Livro de receitas</h1>
                    <p className='mt-4 text-lg'>Crie novas receitas para o seu plano di√°rio</p>
                </div>
                <ul className='flex flex-col gap-3 mt-20 ml-40 p-6 rounded-lg bg-white'>
                    {recipesList.map((recipe, index) => 
                        <li key={index}>
                            {recipe.name}
                        </li>
                    )}
                </ul>
            </div>
        </div>
    )
}

const recipe1 = {
    name: 'recipe1',
    ingredients: [
        {
            name: 'cheese',
            quantity: 2
        },
        {
            name:'bread',
            quantity: 3
        }
    ]
}
const recipe2 = {
    name: 'recipe2',
    ingredients: [
        {
            name: 'butter',
            quantity: 1
        },
        {
            name:'salt',
            quantity: 1
        }
    ]
}

const recipesList = [recipe1, recipe2];

export default Recipes;
