import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { useLocation } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import { getUserByUrl } from "../../services/getUserByUrl";
import { getPostsById } from '../../services/getPostsById'

export const useUserProfile = async() => {
    const { currentUser } = useContext(AuthContext);
    const [followed, setFollowed] = useState(false);
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState();
    const location = useLocation();

    useEffect(() => {

        
        if(user) {
            const posts = getPostsById(user.id);
            setPosts(posts);
            fetchFollowed();
        }
    }, [user])

    const fetchFollowed = async () => {
        try {
            const res = await axios.get('//localhost:5000/follow', {
                params: {
                    followed_user_id: user.id
                }
            })
    
            res.data.forEach((item) => {
                if(item.follower_user_id === currentUser.id) {
                    setFollowed(true);
                } 
            })
        } catch(err) {
            console.error(err);
        }
    }

    const handleFollow = async() => {
        try {
            followed ? 
                axios.delete('//localhost:5000/follow', {
                    data: {
                        follower_user_id: currentUser.id,
                        followed_user_id: user.id
                    }
                }).then(() => setFollowed(false))
            :
                axios.post('//localhost:5000/follow', {
                    follower_user_id: currentUser.id,
                    followed_user_id: user.id
                }).then(() => setFollowed(true))
        } catch(err) {
            console.error(err);
        }
    }

    return { followed, handleFollow, user, posts };
}
